<script>
	//引入封装好的axios请求
	import { instance } from '../utils/request';

	export default {
		name: 'HelloWorld',
		data() {
			return {
				timer: null, //定时器
				active_index: 0, //当前轮播下标
				is_prev: false, //是否点击上一张(控制吃豆人朝向)
				// 轮播图数据(json格式)
				list: [
					{
						title: '尼尔-自动人形',
						image: require('../assets/img/L1.webp'),
						bottom_color: '#000000',
					},
					{
						title: '你在B站的“一时冲动”，让这个世界发生了改变',
						image: require('../assets/img/L2.webp'),
						bottom_color: '#ffe50c',
					},
					{
						title: '坚持!生存下去!贝爷荒岛求生再度升级',
						image: require('../assets/img/L3.webp'),
						bottom_color: '#7F725B',
					},
					{
						title: '《抓娃娃机出货口现状》',
						image: require('../assets/img/L4.webp'),
						bottom_color: '#E3BA5B',
					},
					{
						title: '00后老师都是这么上课嘛?来投稿分享!',
						image: require('../assets/img/L5.webp'),
						bottom_color: '#2D2D27FF',
					},
					{
						title: '看站内优质好片，抽千元大奖!',
						image: require('../assets/img/L6.webp'),
						bottom_color: '#8563C6',
					},
					{
						title: '朱棣在故宫里造的神殿，真有那么灵?',
						image: require('../assets/img/L7.webp'),
						bottom_color: '#886E5AFF',
					},
					{
						title: 'Steam在线人数榜第1名竟然是?',
						image: require('../assets/img/L8.webp'),
						bottom_color: '#305421',
					},
				],
			};
		},
		// 定义事件方法
		methods: {
			//封装好的axios
			fetchData() {
				instance('/rcmd', {})
					.then((response) => {
						console.log(response);
					})
					.catch((error) => {
						console.error('Error fetching data:', error);
					});
			},

			// 停止轮播
			stopAutoPlay() {
				// 清空定时器
				for (let i = 0; i <= this.timer; i++) {
					clearInterval(i);
				}
			},
			// 开始轮播
			startAutoPlay() {
				// 停止轮播
				this.stopAutoPlay();
				let _t = this;
				this.timer = setInterval(function () {
					_t.active_index++;
					if (_t.active_index > _t.list.length - 1) {
						_t.active_index = 0;
					}
					_t.is_prev = false;
					_t.changeBanner(_t.active_index);
				}, 3000);
			},
			// 切换banner 参数:index=轮播下标(点击上一张,下一张按钮时,该值为-1;点击指示器时,该值为对应的轮播下标),is_prev=是否上一张(true为上一张,false为下一张)
			changeBanner(index, is_prev) {
				if (index >= 0) {
					// 点击指示器时
					// 默认是下一张,吃豆人向右
					this.is_prev = false;
					if (index < this.active_index) {
						// 点击时轮播下标小于当前轮播下标时,则为上一张,吃豆人向左
						this.is_prev = true;
					}
					// 设置当前轮播下标
					this.active_index = index;
				} else {
					// 点击按钮时
					if (is_prev) {
						// 上一张
						this.active_index--;
						if (this.active_index < 0) {
							this.active_index = this.list.length - 1;
						}
					} else {
						// 下一张
						this.active_index++;
						if (this.active_index > this.list.length - 1) {
							this.active_index = 0;
						}
					}
					// 指明上一张或下一张
					this.is_prev = is_prev;
				}
				// --m-left,--c-color是css的自定义属性,可通过var函数进行调用
				// 设置偏移量以达到显示指定图片的目的
				document
					.querySelector('.img-box')
					.style.setProperty('--m-left', this.active_index);
				// 设置图片底部的渐变效果
				document
					.querySelector('.bottom-box')
					.style.setProperty(
						'--b-color',
						this.list[this.active_index].bottom_color
					);
			},
		},
		filters: {}, // 定义私有的过滤器
		directives: {}, // 定义私有的指令
		components: {}, // 定义实例内部私有的组件
		watch: {}, // 监听值的变化，然后执行相对应的函数（或者步骤）
		beforeCreate() {}, // 实例刚在内存中被创建出来，还没初始化好 data 和 methods 属性之前调用此函数
		// created() {},
		beforeMount() {}, // 此时已经完成了模板的编译，但是还没有挂载到页面中，在挂载开始之前调用此函数
		mounted() {}, // 此时已经将编译好的模板，挂载到了页面指定的容器中显示。页面（HTML）加载完成之后调用此函数。执行顺序：子组件 -> 父组件
		beforeUpdate() {}, // 状态更新之前调用此函数，此时 data 中的状态值是最新的，但是界面上显示的数据还是旧的，因为此时还没有开始重新渲染 DOM 节点
		updated() {}, // 状态更新完成之后调用此函数，此时 data 中的状态值和界面上显示的数据，都已经完成了更新，界面已经被重新渲染好了
		beforeDestroy() {}, // 实例销毁之前调用此函数。在这一步，实例仍然完全可用
		destroyed() {}, // 实例销毁后调用此函数。该钩子被调用后，对应 Vue 实例的所有指令都被解绑，所有的事件监听器被移除，所有的子实例也都被销毁

		// 实例已经在内存中创建完成，此时 data 和 methods 属性初始化完成，页面（HTML）加载完成之前（未开始编译模板）调用此函数。执行顺序：父组件 -> 子组件
		created() {
			// 初始化,自动轮播
			this.startAutoPlay();
			this.fetchData();
		},
	};
</script>

